package Lab1;

public class Area {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int len=2,b=3;
		
		int area,perimeter;
		
		area = len*b;
		perimeter = 2*(len+b);
		
		System.out.println("Area of rectangle is " + area);
		System.out.println("Perimeter of rectangle is " + perimeter);
	}

}
